
public class myages {

	public static void main(String[] args) {

		// TODO Auto-generated method stub
		for (int i = 1; i < 100; i++) {

			if ((2008 + i) == 2022) {
				System.out.println("I am " + i + " this year");

			} else if (i > 13) {
				System.out.println("I will be " + i + " in " + (2008 + i) + ".");
			} else {
				System.out.println("I was " + i + " in " + (2008 + i) + ".");
			

			}
			

			

		}

	}

}
